package com.iwex.poolpredictor.presentation.resource

import android.graphics.Bitmap
import android.graphics.BitmapFactory
import android.util.Base64

class FloatingIcon {
    companion object {
        private const val BASE64_ENCODED_ICON = ""

        fun getBitmap(): Bitmap {
            val decoded: ByteArray = Base64.decode(BASE64_ENCODED_ICON, Base64.DEFAULT)
            return BitmapFactory.decodeByteArray(decoded, 0, decoded.size)
        }
    }
}
